import { ethers } from "hardhat";
import { loadDeployment } from "@airdao/deployments";
import { ContractNames } from "../../src";
import { ValidatorSet } from "../../typechain-types";


async function main() {
  const TIME_BEFORE_TRANSITION = 1 * 60 * 60; // 1 hour
  const { chainId } = await ethers.provider.getNetwork();
  console.log(await getChainspec(TIME_BEFORE_TRANSITION, chainId));
}

async function getChainspec(time_before_transition: number, chainId: number) {
  const validatorSet = loadDeployment(ContractNames.ValidatorSet, chainId) as ValidatorSet;

  const fees = loadDeployment(ContractNames.Fees, chainId);
  const currentBlock = await ethers.provider.getBlockNumber();
  const transitionBlock = currentBlock + time_before_transition / 5;
  const transitionBlockRounded = Math.ceil(transitionBlock / 1000) * 1000;

  const params = getParams(transitionBlockRounded, validatorSet.address, fees.address);
  if (chainId === 30746)
    return devnet(params);
  if (chainId === 22040)
    return testnet(params);
  return;


}

function getParams(transitionBlock: number, validatorSetAddress: string, feesAddress: string) {
  return `
{
        "stepDuration": "5",
        "blockRewardContractAddress": "0x0000000000000000000000000000000000000F01",
        "blockRewardContractTransition": "0x0",
        "blockRewardContractTransitions": {
          "${transitionBlock}": "${validatorSetAddress}"
        },
        "feesContractTransitions": {
          "${transitionBlock}": "${feesAddress}"
        },
        "blockRewardModeTransitions": {
          "${transitionBlock}": 1
        },
        "blockGasReservedTransitions": {
          "${transitionBlock}": 5000000
        },
        "validators": {
          "multi": {
            "0": {
              "safeContract": "0x0000000000000000000000000000000000000F00"
            },
            "${transitionBlock}": {
              "safeContract": "${validatorSetAddress}"
            }
          }
        }
      }
`;
}

